class PomodoroTimer {
    constructor() {
        this.workTime = 25 * 60; // 25 minutes in seconds
        this.shortBreak = 5 * 60; // 5 minutes in seconds
        this.longBreak = 15 * 60; // 15 minutes in seconds
        this.timeLeft = this.workTime;
        this.isRunning = false;
        this.pomodoroCount = 0;
        this.isWorkTime = true;
        this.interval = null;

        // DOM elements
        this.minutesDisplay = document.getElementById('minutes');
        this.secondsDisplay = document.getElementById('seconds');
        this.startButton = document.getElementById('start');
        this.pauseButton = document.getElementById('pause');
        this.resetButton = document.getElementById('reset');
        this.statusDisplay = document.getElementById('currentStatus');
        this.pomodoroCountDisplay = document.getElementById('pomodoroCount');
        this.toggleModeButton = document.getElementById('toggleMode');

        // Bind event listeners
        this.startButton.addEventListener('click', () => this.start());
        this.pauseButton.addEventListener('click', () => this.pause());
        this.resetButton.addEventListener('click', () => this.reset());
        this.toggleModeButton.addEventListener('click', () => this.toggleMode());

        // Initialize display and button text
        this.updateDisplay();
        this.toggleModeButton.textContent = 'Rest Mode'; // Initially show Rest Mode since we're in Work Mode
    }

    updateDisplay() {
        const minutes = Math.floor(this.timeLeft / 60);
        const seconds = this.timeLeft % 60;
        this.minutesDisplay.textContent = minutes.toString().padStart(2, '0');
        this.secondsDisplay.textContent = seconds.toString().padStart(2, '0');
        this.pomodoroCountDisplay.textContent = `Pomodoros: ${this.pomodoroCount}`;
    }

    toggleMode() {
        // Stop any existing timer
        this.pause();

        // Toggle the mode
        this.isWorkTime = !this.isWorkTime;
        
        // Update the button appearance and text to show the NEXT mode you can switch to
        if (this.isWorkTime) {
            this.toggleModeButton.textContent = 'Rest Mode';
            this.toggleModeButton.classList.remove('rest-mode');
            this.toggleModeButton.classList.add('work-mode');
            this.timeLeft = this.workTime;
            this.statusDisplay.textContent = 'Work Time';
        } else {
            this.toggleModeButton.textContent = 'Work Mode';
            this.toggleModeButton.classList.remove('work-mode');
            this.toggleModeButton.classList.add('rest-mode');
            this.timeLeft = this.shortBreak;
            this.statusDisplay.textContent = 'Break Time';
        }

        // Update the display
        this.updateDisplay();

        // Start the timer automatically
        this.start();
    }

    start() {
        if (!this.isRunning) {
            this.isRunning = true;
            this.interval = setInterval(() => this.tick(), 1000);
            this.startButton.disabled = true;
        }
    }

    pause() {
        if (this.isRunning) {
            this.isRunning = false;
            clearInterval(this.interval);
            this.startButton.disabled = false;
        }
    }

    reset() {
        this.pause();
        this.timeLeft = this.isWorkTime ? this.workTime : this.shortBreak;
        this.statusDisplay.textContent = this.isWorkTime ? 'Work Time' : 'Break Time';
        this.updateDisplay();
    }

    tick() {
        if (this.timeLeft > 0) {
            this.timeLeft--;
            this.updateDisplay();
        } else {
            this.handleTimerComplete();
        }
    }

    handleTimerComplete() {
        // Play notification sound
        const audio = new Audio('data:audio/wav;base64,');
        audio.play();

        // Toggle mode automatically
        this.toggleMode();
    }
}

// Initialize the timer
const pomodoro = new PomodoroTimer(); 